from browser import window
import sys

class StdOutStream:
    def write(self, data):
        window.print(str(data))

    def flush(self):
        pass


class StdErrStream:
    def write(self, data=''):
        window.printErr(str(data))

    def flush(self):
        pass


sys.stdout = StdOutStream()
sys.stderr = StdErrStream()
